mail.yandex.ru.css:
/* Удаляет рекламу слева, если отключён компактный режим */
.ns-view-react-left-column + div, .qa-LeftColumn-Collectors + div {
  display: none;
}
/* Конец блока */


/* Блок удаления верхней плашки рекламы: из него вытекает передвижение других элементов интерфейса [Панель поиска, кнопка настроек, аватар, кнопка 'Все сервисы'] */
.ns-view-advanced-search-box + div {
  display: none;
}

.ns-view-header-box {
  position: absolute;
  top: -25px;
  width: inherit;
}

.ns-view-advanced-search-box {
  top: 50px;
  left: 5px;
}

.PSHeader-Right {
  height: 48px;
}

.mail-SettingsButton {
  padding: 0;
  margin: 0;
  top: 1px;
}

.light-popup {
  top: 38px;
  right: -10px;
}

.search-input__form-activator {
  top: 1px;
  right: 3px;
  margin: 0;
}

.ns-view-more-services {
  padding: 0;
}
/* Конец блока */


/* Убирает ненужные ссылки в самом низу */
.mail-App-Footer {
  display: none;
}
/* Конец блока */


/* Добавляет отступ внизу страницы для симетрии */
.mail-Layout {
  padding-bottom: 20px;
}
/* Конец блока */

*.wikipedia.org.css:
* {
  font-family: Open Sans;
}

#mw-page-base, #mw-head, .mw-editsection, footer, #siteSub, .wb-langlinks-edit {
  display: none;
}

body {
  background-color: white;
}

#content {
  margin: 0 auto;
  width: 900px;
  border: none;
  padding: 30px 0;
}

#contentSub {
  margin-left: 0;
}

#firstHeading {
  border: none;
  font-size: 36px;
}

.hatnote {
  margin-bottom: 20px;
  padding: 0;
  font-style: normal;
}

.infobox, .toc {
  float: none;
  margin: 0;
  width: 100%;
  margin-bottom: 30px;
}

.toc, .toc li {
  font-size: 14px;
}

.mw-headline {
  font-size: 19px;
}

p {
  font-size: 17px;
  line-height: 28px;
  margin: 6px 0 20px;
}

li {
  font-size: 17px;
}

#mw-panel > * {
  display: none;
}

#mw-panel > #p-lang, #mw-panel > #p-coll-print_export {
  display: block;
}

#mw-panel {
  display: block;
  font-size: 13px;
  padding-top: 30px;
}

#mw-panel li {
  font-size: 13px;
}

