yandex.ru.css:
/* Поиск и другие сервисы */
.incut, .distr-nav, .yaplus {
  display: none;
}
/* Конец блока */


/* Яндекс Картинки */
.b-statcounter + div {
  display: none;
}
/* Конец блока */


/* Яндекс Погода */
.content__bottom + div, .content__bottom + div + div, .card_without-card-decoration, .promo {
  display: none;
}
.content[role=main] > div {
  margin: 10px;
}
footer {
  display: none;
}
/* Конец блока */

mail.yandex.ru.css:
/* Удаляет рекламу слева, если отключён компактный режим */
.ns-view-react-left-column + div, .qa-LeftColumn-Collectors + div {
  display: none;
}
/* Конец блока */


/* Блок удаления верхней плашки рекламы: из него вытекает передвижение других элементов интерфейса [Панель поиска, кнопка настроек, аватар, кнопка 'Все сервисы'] */
.ns-view-advanced-search-box + div {
  display: none;
}

.ns-view-header-box {
  position: absolute;
  top: -25px;
  width: inherit;
}

.ns-view-advanced-search-box {
  top: 50px;
  left: 5px;
}

.PSHeader-Right {
  height: 48px;
}

.mail-SettingsButton {
  padding: 0;
  margin: 0;
  top: 1px;
}

.light-popup {
  top: 38px;
  right: -10px;
}

.search-input__form-activator {
  top: 1px;
  right: 3px;
  margin: 0;
}

.ns-view-more-services {
  padding: 0;
}
/* Конец блока */


/* Убирает ненужные ссылки в самом низу */
.mail-App-Footer {
  display: none;
}
/* Конец блока */


/* Добавляет отступ внизу страницы для симетрии */
.mail-Layout {
  padding-bottom: 20px;
}
/* Конец блока */
